<node TEXT="def read(*filenames, **kwargs):" ID="ID_1985089913" CREATED="1406792949861" MODIFIED="1406792949861">
<node TEXT="encoding = kwargs.get(&apos;encoding&apos;, &apos;utf-8&apos;)" ID="ID_1507683132" CREATED="1406792949861" MODIFIED="1406792949861"/>
<node TEXT="sep = kwargs.get(&apos;sep&apos;, &apos;\n&apos;)" ID="ID_368793058" CREATED="1406792949861" MODIFIED="1406792949861"/>
<node TEXT="buf = []" ID="ID_1703766629" CREATED="1406792949861" MODIFIED="1406792949861"/>
<node TEXT="for filename in filenames:" ID="ID_1443552407" CREATED="1406792949861" MODIFIED="1406792949861">
<node TEXT="with io.open(filename, encoding=encoding) as f:" ID="ID_1248976877" CREATED="1406792949861" MODIFIED="1406792949861">
<node TEXT="buf.append(f.read())" ID="ID_107180876" CREATED="1406792949861" MODIFIED="1406792949861"/>
</node>
</node>
<node TEXT="return sep.join(buf)" ID="ID_1334657473" CREATED="1406792949861" MODIFIED="1406792949861"/>
</node>
