<node TEXT="self.test_args = []" ID="ID_90747763" CREATED="1406792949878" MODIFIED="1406792949878"/>
<node TEXT="self.test_suite = True" ID="ID_335630882" CREATED="1406792949878" MODIFIED="1406792949878"/>
</node>
<node TEXT="def run_tests(self):" ID="ID_708194796" CREATED="1406792949878" MODIFIED="1406792949878">
